import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
// BACKEND PLACEHOLDER: Import useAuth
import { useAuth } from '../../context/AuthContext'; 
import './AdminHeader.css'; 

const AdminHeader = () => {
    // ðŸ›‘ BACKEND PLACEHOLDER: Uncomment the context hook
    const { user, logout } = useAuth();
    const navigate = useNavigate();

    // MOCK DATA for testing UI only:
    const username = user?.displayName || user?.email.split('@')[0] || "Admin";  

    const handleLogout = () => {
        logout(); 
        navigate('/login', { replace: true });
    };

    return (
        <header className="admin-header">
            {/* Left Section: Logo and Navigation Links */}
            <div className="header-left">
                
                {/* Logo/Home Link (Routes to Admin Dashboard home) */}
                <Link to="/admin" className="admin-logo-link">
                    LostHub
                </Link>
                
                {/* Admin Navigation Link */}
                <nav className="header-nav">
                    <Link to="/admin/usermanage" className="nav-link">
                        User Management
                    </Link>
                </nav>
            </div>

            {/* Right Side: Admin Info and Logout */}
                    <div className="admin-info">
                        {/* Person Icon and Text */}
                        <div className="admin-badge">
                            {/* Replaced Lucide User icon with PNG */}
                            <img src={PersonIcon} alt="Admin Icon" className="admin-icon" /> 
                            <span className="admin-text">Admin</span>
                        </div>
                        
                        {/* Logout Button */}
                        <a 
                            href="/login" // Link back to login page
                            className="logout-btn"
                        >
                            <LogOut size={16} />
                            <span>Log Out</span>
                        </a>
            </div>
        </header>
    );
};

export default AdminHeader;
