import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom'; 
import ItemDetailsModal from '../../components/items/ItemDetailsModal';
import './AllPosts.css'; // For layout and styling
// ðŸ›‘ BACKEND PLACEHOLDER: Import axios for API calls
// import axios from 'axios'; 
// ðŸ›‘ BACKEND PLACEHOLDER: Import useAuth to get token and displayName
// import { useAuth } from '../../context/AuthContext';

// Utility function to format timestamp from Firestore
const formatTimestamp = (timestamp) => {
    // ðŸ›‘ BACKEND PLACEHOLDER: This logic will handle Firebase Timestamp objects.
    if (!timestamp || !timestamp.toDate) {
        // Fallback for mock data (assuming mock uses standard Date objects)
        return timestamp instanceof Date 
            ? timestamp.toLocaleDateString('en-US', { year: 'numeric', month: 'short', day: 'numeric' })
            : 'N/A';
    }
    // Logic for actual Firebase Timestamp object
    return timestamp.toDate().toLocaleDateString('en-US', { year: 'numeric', month: 'short', day: 'numeric' });
};

// Item Categories (Used in the dropdown)
const CATEGORIES = ['Electronics', 'Stationery', 'Books', 'Clothing', 'Personal Items', 'Others'];

// ðŸ›‘ BACKEND PLACEHOLDER: Mock Data Set (for testing UI transition)
const ALL_MOCK_ITEMS = [
    { id: 1, name: "Student ID Card", category: "Personal Items", status: "lost", dateReported: new Date('2025-11-20'), imageUrl: "https://via.placeholder.com/200x150/0000FF/808080?Text=LOST+ID", lastSeenLocation: "Library Entrance, Level 2", description: "Blue card holder, urgent!", isApproved: true },
    { id: 2, name: "Purple Water Bottle", category: "Personal Items", status: "found", dateReported: new Date('2025-11-25'), imageUrl: "https://via.placeholder.com/200x150/FF00FF/FFFFFF?Text=FOUND+Bottle", lastSeenLocation: "Lecture Hall B", whereToCollect: "Security Office, Block A", description: "Found near the main lecture podium.", isApproved: true },
    { id: 3, name: "Programming Textbook", category: "Books", status: "lost", dateReported: new Date('2025-11-28'), imageUrl: "https://via.placeholder.com/200x150/00AAFF/FFFFFF?Text=LOST+Book", lastSeenLocation: "Cafeteria", description: "Hardcover, titled 'Intro to React'.", isApproved: true },
    { id: 4, name: "Blue Hoodie", category: "Clothing", status: "found", dateReported: new Date('2025-11-29'), imageUrl: "https://via.placeholder.com/200x150/00FF00/000000?Text=FOUND+Hoodie", lastSeenLocation: "Sports Field", whereToCollect: "Residence Hall Lobby", description: "Size L, brand name visible.", isApproved: true },
    { id: 5, name: "Red USB Drive", category: "Electronics", status: "lost", dateReported: new Date('2025-11-30'), imageUrl: "https://via.placeholder.com/200x150/FF0000/FFFFFF?Text=LOST+USB", lastSeenLocation: "CS Lab", description: "Small red drive, contains project files.", isApproved: true },
];

// ðŸ›‘ BACKEND PLACEHOLDER: API URL
// const ITEMS_API_URL = 'http://localhost:3000/api/items';

// --- STATIC HEADER PLACEHOLDER with BACKEND LOGIC---
const StaticHeader = () => {
    // ðŸ›‘ BACKEND PLACEHOLDER: Uncomment this line to get user data from context
    // const { user, logout } = useAuth();

    // ðŸ›‘ BACKEND PLACEHOLDER: Replace "TestUser" with the user's actual display name
    // const username = user?.displayName || user?.email.split('@')[0] || "User"; 
    const username = "TestUser"; // MOCK USERNAME

    // ðŸ›‘ BACKEND PLACEHOLDER: Replace a href with navigate(to) or use the actual logout function
    const handleLogout = () => {
        // logout(); 
        console.log("SIMULATION: Logging out...");
    };

    return (
    <header className="main-header"> {/* Use a common class for the header container */}
        
        {/* Left Section: Logo and Navigation Links */}
        <div className="header-left">
            <a href="/posts" className="header-logo-link">LostHub</a>
            
            <nav className="header-nav">
                <a href="/report/lost" className="nav-link">Report Lost</a>
                <a href="/report/found" className="nav-link">Report Found</a>
            </nav>
        </div>

        {/* Right Section: Welcome Message and Logout */}
        <div className="header-right">
            <span className="welcome-message">
                Welcome, <strong>TestUser</strong>
            </span>
            <a href="/login" className="logout-button-link">
                Logout
            </a>
        </div>
    </header>
    );
};
// -----------------------------------------------------------------

const AllPosts = () => {
    const [statusFilter, setStatusFilter] = useState('lost'); // Default: Lost Item view
    const [categoryFilter, setCategoryFilter] = useState(''); 
    const [items, setItems] = useState([]);
    const [isLoading, setIsLoading] = useState(false);
    const [selectedItem, setSelectedItem] = useState(null); // State for Modal

    // ðŸ›‘ BACKEND PLACEHOLDER: Get token for API headers
    // const { token } = useAuth();

    // BACKEND PLACEHOLDER: useEffect needs to be replaced with a real data fetch function
    useEffect(() => {
        // Function to fetch data from the API
        const fetchItems = async () => {
        setIsLoading(true);
        
        // ðŸ›‘ BACKEND PLACEHOLDER: Query parameters for the backend
            /*
            const params = {
                status: statusFilter,
                category: categoryFilter || undefined,
            };

            try {
                // ðŸ›‘ BACKEND PLACEHOLDER: UNCOMMENT this block to use the backend
                // const response = await axios.get(ITEMS_API_URL, {
                //     params,
                //     headers: { Authorization: `Bearer ${token}` } // Send JWT token
                // });
                // setItems(response.data); 

            } catch (error) {
                console.error("Failed to fetch items:", error);
                setItems([]); // Clear items on failure
            } finally {
                setIsLoading(false);
            }
            */

        // --- MOCK DATA FALLBACK (Currently Running) ---
        const filteredData = ALL_MOCK_ITEMS.filter(item => {
            const statusMatch = item.status === statusFilter;
            const categoryMatch = !categoryFilter || item.category === categoryFilter;
            return statusMatch && categoryMatch;
        });
        
        // Simulate network delay
        setTimeout(() => {
            setItems(filteredData);
            setIsLoading(false);
        }, 500);
    };
    // ðŸ›‘ BACKEND PLACEHOLDER: Add token dependency when uncommenting API call
        fetchItems(); 
    }, [statusFilter, categoryFilter /*, token */ ]);

    const handleItemClick = (item) => {
        setSelectedItem(item);
    };

    return (
        <div className="all-posts-page">
            <StaticHeader /> 
            
            <main className="posts-main-content">
                
                {/* Top Control Bar: Filters */}
                <div className="posts-control-bar">
                    
                    {/* 1. Category Dropdown */}
                    <select 
                        value={categoryFilter} 
                        onChange={(e) => setCategoryFilter(e.target.value)} 
                        className="category-dropdown"
                    >
                        <option value="">All Categories</option>
                        {CATEGORIES.map(cat => <option key={cat} value={cat}>{cat}</option>)}
                    </select>

                    {/* 2. Status Toggle Buttons */}
                    <div className="status-toggle-group">
                        <button 
                            onClick={() => setStatusFilter('lost')}
                            className={`status-button ${statusFilter === 'lost' ? 'active-status' : ''}`}
                        >
                            Lost Item
                        </button>
                        <button 
                            onClick={() => setStatusFilter('found')}
                            className={`status-button ${statusFilter === 'found' ? 'active-status' : ''}`}
                        >
                            Found Item
                        </button>
                    </div>
                </div>

                {/* Item Grid */}
                {isLoading ? (
                    <div className="loading-state">Loading items from Firebase...</div>
                ) : (
                    <div className="item-grid-container">
                        {items.length > 0 ? (
                            items.map(item => (
                                <div key={item.id} className="item-card" onClick={() => handleItemClick(item)}>
                                    <img 
                                        src={item.imageUrl} 
                                        alt={item.name} 
                                        className="item-photo"
                                    />
                                    <div className="item-info">
                                        <h3>{item.name}</h3>
                                        <p>Reported: {formatTimestamp(item.dateReported)}</p>
                                    </div>
                                </div>
                            ))
                        ) : (
                            <p className="no-items-message">No {statusFilter} items found in this category.</p>
                        )}
                    </div>
                )}
            </main>

            {/* Item Details Modal */}
            <ItemDetailsModal item={selectedItem} onClose={() => setSelectedItem(null)} />
        </div>
    );
};

export default AllPosts;
